INSERT INTO `tblnovel` (`id`, `sNovel`, `sCover`, `sDes`, `dCreateDay`, `dUpdateDay`, `sProgress`, `iStatus`, `idUser`, `iLicense_Status`, `sLicense`) VALUES
(17, 'Tiên Mộ', 'time_1722136017_file_300.jpg', '&lt;p&gt;Mười vạn năm trước, chư ti&amp;ecirc;n đại chiến, vạn ti&amp;ecirc;n vẫn diệt, ti&amp;ecirc;n đạo đoạn tuyệt, Ti&amp;ecirc;n Giới ở trong ti&amp;ecirc;n mộ san s&amp;aacute;t.&lt;br /&gt;\r\n&lt;br /&gt;\r\nMười vạn năm về sau, Mạc Kim Gi&amp;aacute;o &amp;Uacute;y mang theo [ Sinh Tử Thi&amp;ecirc;n Thư ] gi&amp;aacute;ng l&amp;acirc;m Ti&amp;ecirc;n Giới.&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;quot;Trộm mộ thế nhưng l&amp;agrave; một m&amp;ocirc;n nghệ thuật, ca đến n&amp;oacute;i cho c&amp;aacute;c ngươi biết trộm mộ ch&amp;iacute;nh x&amp;aacute;c thao t&amp;aacute;c phương thức.&amp;quot; Lục V&amp;acirc;n nh&amp;igrave;n xem trong ti&amp;ecirc;n mộ luống cuống tay ch&amp;acirc;n Ti&amp;ecirc;n Nh&amp;acirc;n, ngưu kh&amp;iacute; hống hống n&amp;oacute;i. . . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nP/S: Truyện đ&amp;atilde; được chuyển thể th&amp;agrave;nh phim 3D&lt;br /&gt;\r\n&lt;br /&gt;\r\n------ Hệ Thống Tu Luyện ------&lt;br /&gt;\r\n_ Kh&amp;iacute; Cảnh: Luyện Kh&amp;iacute; - Ngưng Kh&amp;iacute; - H&amp;oacute;a Kh&amp;iacute;.&lt;br /&gt;\r\n_ Đan Cảnh: Kim Đan, Mệnh Đan, Nguy&amp;ecirc;n Đan.&lt;br /&gt;\r\n_ Thần Cảnh: Nguy&amp;ecirc;n Thần - Luyện Thần - H&amp;oacute;a Thần.&lt;br /&gt;\r\n_Hư Cảnh: Khuy Hư - Động Hư - Phản Hư.&lt;br /&gt;\r\n_Ch&amp;acirc;n Ti&amp;ecirc;n&lt;br /&gt;\r\n_Thi&amp;ecirc;n Ti&amp;ecirc;n&lt;br /&gt;\r\n_Thượng Ti&amp;ecirc;n&lt;br /&gt;\r\n_Kim Ti&amp;ecirc;n&lt;br /&gt;\r\n_Huyền Ti&amp;ecirc;n&lt;br /&gt;\r\n_Ch&amp;iacute; Ti&amp;ecirc;n&lt;br /&gt;\r\n_Quả Vị: La Thi&amp;ecirc;n Quả Vị - Huyền Thi&amp;ecirc;n Quả Vị - Hỗn Nguy&amp;ecirc;n Quả Vị.&lt;br /&gt;\r\n_Th&amp;ocirc;ng Thi&amp;ecirc;n&lt;br /&gt;\r\n_Nguy&amp;ecirc;n Thủy&lt;br /&gt;\r\n_Th&amp;aacute;i Thượng&lt;br /&gt;\r\n_Th&amp;aacute;i Thanh&lt;br /&gt;\r\n_Ngọc Thanh ( Nh&amp;acirc;n Vương cảnh )&lt;br /&gt;\r\n_Đại đế ( Thượng Thanh cảnh )&lt;br /&gt;\r\nỞ nguy&amp;ecirc;n thủy đại đạo cảnh giới l&amp;agrave;&lt;br /&gt;\r\n_Ti&amp;ecirc;n Thi&amp;ecirc;n đại đạo ứng với Kh&amp;iacute; - Đan - Thần - Hư, 4 cảnh.&lt;br /&gt;\r\n_Kim Đan đại đạo ứng với Ti&amp;ecirc;n cảnh ( từ Ch&amp;acirc;n Ti&amp;ecirc;n tới Ch&amp;iacute; Ti&amp;ecirc;n )&lt;br /&gt;\r\n_Ch&amp;acirc;n Thần cảnh ứng với Th&amp;ocirc;ng Thi&amp;ecirc;n - Nguy&amp;ecirc;n Thủy - Th&amp;aacute;i Thượng cảnh.&lt;br /&gt;\r\n_Thi&amp;ecirc;n Thần l&amp;agrave; Th&amp;aacute;i Thanh cảnh&lt;br /&gt;\r\n_Thần Vương l&amp;agrave; Ngọc Thanh cảnh&lt;br /&gt;\r\n_Thi&amp;ecirc;n Địa cảnh (đại thần th&amp;ocirc;ng giả ) l&amp;agrave; Đại Đế cảnh.&lt;br /&gt;\r\n_Hỗn Độn cảnh (Ti&amp;ecirc;n thi&amp;ecirc;n ma thần ) ch&amp;iacute;nh l&amp;agrave; si&amp;ecirc;u việt đại đế.&lt;br /&gt;\r\n_Tạo Vật Chủ&lt;br /&gt;\r\n_Bất Hủ Chi Vương&lt;br /&gt;\r\n_Vĩnh Hằng Chi Chủ&lt;br /&gt;\r\nNh&amp;acirc;n đạo th&amp;agrave;nh lập th&amp;igrave; cảnh giới giống với Nguy&amp;ecirc;n thủy đại đạo nhưng l&amp;agrave; do nh&amp;acirc;n tộc l&amp;agrave;m chủ l&amp;ecirc;n đổi t&amp;ecirc;n từ thần th&amp;agrave;nh nh&amp;acirc;n ( Ch&amp;acirc;n Thần - Ch&amp;acirc;n Nh&amp;acirc;n, Thi&amp;ecirc;n Thần - Thi&amp;ecirc;n Nh&amp;acirc;n, Thần Vương - Nh&amp;acirc;n Vương ).&lt;br /&gt;\r\nQuả Vị cảnh giới xuất hiện khi nh&amp;acirc;n đạo sụp đổ v&amp;agrave; Ti&amp;ecirc;n đạo th&amp;agrave;nh lập.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/tim-kiem?q=Ti%C3%AAn%20M%E1%BB%99&lt;/strong&gt;&lt;/p&gt;', '2024-11-01 02:26:03', '2024-11-16 02:29:32', 1, 1, 40, 1, 'time_1722136017_file_banquyen.pdf'),
(18, 'Thanh Sơn', 'time_1722137318_file_Thanh Sơn.jpg', '&lt;p&gt;Lạc th&amp;agrave;nh trong m&amp;agrave;n đ&amp;ecirc;m tuấn m&amp;atilde; c&amp;ugrave;ng thiếu ni&amp;ecirc;n lang, m&amp;oacute;ng ngựa tại b&amp;agrave;n đ&amp;aacute; xanh b&amp;ecirc;n tr&amp;ecirc;n giẫm ra cộc cộc tiếng vang.&lt;br /&gt;\r\n&lt;br /&gt;\r\nHắn phảng phất Thuyết Thư ti&amp;ecirc;n sinh cố sự b&amp;ecirc;n trong nh&amp;acirc;n vật, theo m&amp;acirc;y th&amp;aacute;c nước b&amp;ecirc;n trong đến, hướng trong giang hồ chỗ đi, đi tới thanh sơn, xem r&amp;aacute;ng chiều lặn về ph&amp;iacute;a t&amp;acirc;y.&lt;br /&gt;\r\n&lt;br /&gt;\r\nNếu ngươi hỏi, ai l&amp;agrave; n&amp;agrave;y trong giang hồ bất quy kh&amp;aacute;ch?&lt;br /&gt;\r\n&lt;br /&gt;\r\nHắn sẽ đ&amp;aacute;p, thanh phong, trăng s&amp;aacute;ng, ta.&lt;br /&gt;\r\n&lt;br /&gt;\r\n. . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nĐ&amp;acirc;y c&amp;oacute; lẽ l&amp;agrave; một c&amp;aacute;i d&amp;agrave;i đằng đẵng chuyện xưa, đợi ta chậm r&amp;atilde;i n&amp;oacute;i&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/thanh-son-1&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:29:40', '2024-11-01 11:27:17', 1, 1, 40, 1, 'time_1722137318_file_banquyen.pdf'),
(19, 'Đại Đạo Chi Thượng', 'time_1722138076_file_Đại Đạo Chi Thượng.jpg', '&lt;p&gt;Gia gia của ta rất cổ qu&amp;aacute;i, hắn mỗi ng&amp;agrave;y cho m&amp;igrave;nh d&amp;acirc;ng hương, đứng tại trước linh vị của m&amp;igrave;nh ăn ngọn nến.&lt;br /&gt;\r\n&lt;br /&gt;\r\nNgười trong th&amp;ocirc;n đều rất sợ gia gia.&lt;br /&gt;\r\n&lt;br /&gt;\r\nTa cũng rất sợ gia gia.&lt;br /&gt;\r\n&lt;br /&gt;\r\nVề sau ta mới ph&amp;aacute;t hiện, bọn hắn sợ sệt kh&amp;ocirc;ng phải gia gia, m&amp;agrave; l&amp;agrave; ta.&lt;br /&gt;\r\n&lt;br /&gt;\r\nGia gia cũng rất sợ ta.&lt;br /&gt;\r\n&lt;br /&gt;\r\n. . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nTa gọi Trần Thực.&lt;br /&gt;\r\n&lt;br /&gt;\r\nTrần trong th&amp;agrave;nh thật, Thực trong đ&amp;agrave;ng ho&amp;agrave;ng .&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/dai-dao-chi-thuong&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:29:48', '2024-11-01 11:39:23', 1, 1, 41, 1, 'time_1722138076_file_banquyen.pdf'),
(20, 'Running Man: Ai Mời Hắn Đến? Hắn Thế Nhưng Là Đạo Sĩ A', 'time_1722138844_file_123.jpg', '&lt;p&gt;Biệt danh &amp;laquo; Running Man: Để ngươi khi kh&amp;aacute;ch qu&amp;yacute;, kh&amp;ocirc;ng c&amp;oacute; để ngươi c&amp;aacute;ch l&amp;agrave;m! &amp;raquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\nĐạo sĩ L&amp;acirc;m Thần xuy&amp;ecirc;n việt thế giới song song, trở th&amp;agrave;nh Running Man ngoại tr&amp;agrave;ng kh&amp;aacute;ch qu&amp;yacute;, ngo&amp;agrave;i &amp;yacute; muốn giải tỏa thần cấp lựa chọn hệ thống!&lt;br /&gt;\r\n&lt;br /&gt;\r\nĐen kịt trong m&amp;ecirc; cung.&lt;br /&gt;\r\n&lt;br /&gt;\r\nLựa chọn &amp;laquo; sử dụng ch&amp;uacute; thuật truy tung minh tinh vị tr&amp;iacute;, ban thưởng: Tuổi thọ +10 năm! &amp;raquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\nLựa chọn &amp;laquo; sử dụng khinh c&amp;ocirc;ng x&amp;eacute; nh&amp;atilde;n hiệu, ban thưởng: V&amp;ocirc; hạn thể lực! &amp;raquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\nLựa chọn &amp;laquo; sử dụng t&amp;iacute;nh mệnh ph&amp;aacute; giải c&amp;acirc;u đố, ban thưởng: Thần cấp may mắn kỹ năng! &amp;raquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\n. . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nL&amp;uacute;c đầu, mọi người cho l&amp;agrave; hắn chỉ l&amp;agrave; c&amp;aacute;i vật l&amp;agrave;m nền d&amp;ugrave;ng phổ th&amp;ocirc;ng tiểu nghệ nh&amp;acirc;n!&lt;br /&gt;\r\n&lt;br /&gt;\r\nKh&amp;ocirc;ng nghĩ tới!&lt;br /&gt;\r\n&lt;br /&gt;\r\nBởi v&amp;igrave; hắn kh&amp;ocirc;ng hợp th&amp;oacute;i thường thao t&amp;aacute;c, nh&amp;acirc;n kh&amp;iacute; một đường tăng vọt, hắn trực tiếp từ ngoại tr&amp;agrave;ng kh&amp;aacute;ch qu&amp;yacute;, trở th&amp;agrave;nh thường tr&amp;uacute; kh&amp;aacute;ch qu&amp;yacute;!&lt;br /&gt;\r\n&lt;br /&gt;\r\nNgười xem gọi thẳng: Kh&amp;ocirc;ng c&amp;oacute; L&amp;acirc;m Thần ch&amp;uacute;ng ta kh&amp;ocirc;ng nh&amp;igrave;n tiết mục!&lt;br /&gt;\r\n&lt;br /&gt;\r\nMột đ&amp;aacute;m minh tinh cũng đều bị sợ ng&amp;acirc;y người!&lt;br /&gt;\r\n&lt;br /&gt;\r\nĐặng Sao: Trời ơi! Thế n&amp;agrave;y sao lại l&amp;agrave; kh&amp;aacute;ch qu&amp;yacute; a, đ&amp;acirc;y kh&amp;ocirc;ng phải liền l&amp;agrave; ti&amp;ecirc;n nh&amp;acirc;n sao!&lt;br /&gt;\r\n&lt;br /&gt;\r\nTrịnh Khải: Đừng c&amp;oacute; lại gọi ta tiểu b&amp;aacute;o săn, tiểu b&amp;aacute;o săn cũng sợ ph&amp;ugrave; ch&amp;uacute; Định Th&amp;acirc;n Thuật a!&lt;br /&gt;\r\n&lt;br /&gt;\r\nL&amp;yacute; th&amp;acirc;n: Kh&amp;ocirc;ng s&amp;aacute;nh bằng, căn bản kh&amp;ocirc;ng s&amp;aacute;nh bằng! Hắn nh&amp;igrave;n cao cao gầy g&amp;ograve;, kh&amp;iacute; lực l&amp;agrave;m sao lớn như vậy a!&lt;br /&gt;\r\n&lt;br /&gt;\r\nTrần Hạ: Ai mời hắn đến? Hắn nhưng l&amp;agrave; đạo sĩ a!&lt;br /&gt;\r\n&lt;br /&gt;\r\nK&amp;ecirc; ca: Ai u, ngươi l&amp;agrave;m g&amp;igrave;!&lt;br /&gt;\r\n&lt;br /&gt;\r\nBạch Lộ: Oa tiểu ca ca rất đẹp a!&lt;br /&gt;\r\n&lt;br /&gt;\r\nNhiệt Ba: X&amp;eacute; c&amp;aacute;i nh&amp;atilde;n hiệu đem ph&amp;ugrave; ch&amp;uacute; đều m&amp;oacute;c ra, ch&amp;uacute;ng ta đều ngốc!&lt;br /&gt;\r\n&lt;br /&gt;\r\nDương Mật: Đ&amp;acirc;y c&amp;oacute; phải hay kh&amp;ocirc;ng l&amp;agrave; c&amp;oacute; ch&amp;uacute;t qu&amp;aacute; bất hợp l&amp;yacute;?&lt;br /&gt;\r\n&lt;br /&gt;\r\nBaby: Đạo diễn! Kh&amp;ocirc;ng c&amp;ocirc;ng bằng! Hắn một mực l&amp;agrave; qu&amp;aacute;n qu&amp;acirc;n!&lt;br /&gt;\r\n&lt;br /&gt;\r\nHậu trường, đạo diễn xoa xoa c&amp;aacute;i tr&amp;aacute;n mồ h&amp;ocirc;i: Ai mời ngoại tr&amp;agrave;ng kh&amp;aacute;ch qu&amp;yacute;?&lt;br /&gt;\r\n&lt;br /&gt;\r\nN&amp;oacute;i xong d&amp;ugrave;ng để vật l&amp;agrave;m nền minh tinh, hắn vậy m&amp;agrave; trực tiếp th&amp;agrave;nh đỉnh chảy!&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn :&amp;nbsp;https://metruyencv.com/truyen/running-man-ai-moi-han-den-han-the-nhung-la-dao-si-a&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 03:14:24', '2024-11-16 03:14:24', 1, 1, 40, 1, 'time_1722138844_file_banquyen.pdf'),
(21, 'Từ Hài Nhi Bắt Đầu Nhập Đạo', 'time_1722139233_file_Từ Hài Nhi Bắt Đầu Nhập Đạo.jpg', '&lt;p&gt;Loạn thế cầu sinh, thịnh thế cầu t&amp;ecirc;n.&lt;br /&gt;\r\n&lt;br /&gt;\r\nD&amp;atilde;i dầu sương gi&amp;oacute; Đại Vũ triều vẫn ở v&amp;agrave;o thịnh thế thời k&amp;igrave;.&lt;br /&gt;\r\n&lt;br /&gt;\r\nT&amp;acirc;y Bắc, c&amp;oacute; thiếu ni&amp;ecirc;n đi bộ ch&amp;iacute;n ng&amp;agrave;n dặm, độc th&amp;acirc;n trảm đại y&amp;ecirc;u, tranh một đời c&amp;ocirc;ng danh.&lt;br /&gt;\r\n&lt;br /&gt;\r\nLong Hồ, c&amp;oacute; Bất Thế T&amp;ocirc;ng Sư đi nh&amp;acirc;n gian, khai t&amp;ocirc;ng lập ph&amp;aacute;i, mu&amp;ocirc;n đời lưu danh.&lt;br /&gt;\r\n&lt;br /&gt;\r\nĐế đ&amp;ocirc;, c&amp;oacute; Th&amp;aacute;nh Nh&amp;acirc;n truyền đạo ng&amp;agrave;n vạn, gi&amp;aacute;o đồ trải rộng c&amp;aacute;c ch&amp;acirc;u, lưu danh thi&amp;ecirc;n cổ.&lt;br /&gt;\r\n&lt;br /&gt;\r\nM&amp;agrave; tại Thanh Ch&amp;acirc;u th&amp;agrave;nh b&amp;ecirc;n trong, một chỗ trong tiểu viện.&lt;br /&gt;\r\n&lt;br /&gt;\r\nC&amp;oacute; người lại chỉ muốn y&amp;ecirc;n lặng, hạ xong mỗi một b&amp;agrave;n cờ.&lt;br /&gt;\r\n&lt;br /&gt;\r\n. . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nKiếm l&amp;ecirc;n Long Y&amp;ecirc;n ba ng&amp;agrave;n dặm, một kiếm ch&amp;eacute;m hết Cửu Ch&amp;acirc;u th&amp;agrave;nh!&lt;br /&gt;\r\n&lt;br /&gt;\r\nThế gian vạn ph&amp;aacute;p, duy ta, nhập đạo.&lt;br /&gt;\r\n&lt;br /&gt;\r\n. . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nQuyển s&amp;aacute;ch lại t&amp;ecirc;n &amp;laquo; vạn thế chi danh &amp;raquo; &amp;laquo; kh&amp;ocirc;ng thấy ngươi luyện kiếm, ngươi l&amp;agrave;m sao lại th&amp;agrave;nh Kiếm Thần &amp;raquo; &amp;laquo; Hạo Thi&amp;ecirc;n Đế &amp;raquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\n【 nhẹ nh&amp;otilde;m h&amp;agrave;i hước ] 【 v&amp;otilde; đạo bảng ] 【 nghệ thuật đại sư ]&lt;br /&gt;\r\n&lt;br /&gt;\r\n☆☆☆ Hệ thống tu luyện ☆☆☆&lt;br /&gt;\r\n1- Th&amp;ocirc;ng Lực&lt;br /&gt;\r\n2- Chu Thi&amp;ecirc;n&lt;br /&gt;\r\n3- Kế Hồn&lt;br /&gt;\r\n4- Thần Du&lt;br /&gt;\r\n5- Thập Ngũ L&amp;yacute;&lt;br /&gt;\r\n6- Thi&amp;ecirc;n Nh&amp;acirc;n&lt;br /&gt;\r\n7- Tam Bất Hủ (bất ph&amp;ocirc;i, bất diệt, bất suy)&lt;br /&gt;\r\n8- Tứ Lập (đạo t&amp;acirc;m, nghịch mệnh, tuyệt học, th&amp;aacute;i b&amp;igrave;nh đạo cảnh)&lt;br /&gt;\r\n9- Văn Đạo&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/tu-hai-nhi-bat-dau-nhap-dao&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:30:05', '2024-11-01 11:39:19', 1, 1, 41, 1, 'time_1722139233_file_banquyen.pdf'),
(22, 'Hoàng Hôn Phân Giới', 'time_1722139478_file_Hoàng Hôn Phân Giới.jpg', '&lt;p&gt;Chết thật tốt, bị người cưỡng &amp;eacute;p sống lại.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/hoang-hon-phan-gioi&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:30:12', '2024-11-01 11:39:21', 1, 1, 41, 1, 'time_1722139478_file_banquyen.pdf'),
(23, 'Thôn Phệ Tinh Không 2 Khởi Nguyên Đại Lục', 'time_1722139655_file_Thôn Phệ Tinh Không 2 Khởi Nguyên Đại Lục.jpg', '&lt;p&gt;La Phong mang theo Giới Th&amp;uacute; Ma La T&amp;aacute;t x&amp;ocirc;ng qua lu&amp;acirc;n hồi, đi tới Khởi Nguy&amp;ecirc;n đại lục...&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/thon-phe-tinh-khong-2-khoi-nguyen-dai-luc&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;', '2024-11-16 02:30:19', '2024-11-02 11:28:06', 1, 1, 40, 1, 'time_1722139655_file_banquyen.pdf'),
(24, 'Võng Du Chi Ta Có 10 Lần Tốc Độ Đánh', 'time_1722139820_file_Võng Du Chi Ta Có 10 Lần Tốc Độ Đánh.jpg', '&lt;p&gt;C&amp;ocirc;ng Nguy&amp;ecirc;n 2020 năm, Li&amp;ecirc;n Hợp Quốc khai th&amp;aacute;c V&amp;otilde;ng Du 【 Thần Vực 】 chấn động Open Beta, kim tệ hối đo&amp;aacute;i hiện thực tiền, hấp dẫn to&amp;agrave;n cầu v&amp;ocirc; số người chơi. Thế giới c&amp;aacute;c quốc gia, v&amp;igrave; tranh đoạt du h&amp;iacute; t&amp;agrave;i nguy&amp;ecirc;n đ&amp;middot;&amp;aacute;nh đ&amp;middot;ập t&amp;agrave;n nhẫn.&lt;br /&gt;\r\n&lt;br /&gt;\r\nTần Phong l&amp;agrave; một g&amp;atilde; 【 Thần Vực 】 người chơi, bất qu&amp;aacute;, hắn sở hữu hạng nhất si&amp;ecirc;u thần thi&amp;ecirc;n ph&amp;uacute; -- thập bội tốc độ đ&amp;aacute;nh!&lt;br /&gt;\r\n&lt;br /&gt;\r\nL&amp;agrave;m kh&amp;aacute;c người chơi vẫn c&amp;ograve;n ở chậm r&amp;atilde;i đ&amp;aacute;nh tiểu qu&amp;aacute;i l&amp;uacute;c, Tần Phong thập bội tốc độ đ&amp;aacute;nh, đ&amp;atilde; ung dung c&amp;aacute;ch l&amp;agrave;m rơi BOSS.&lt;br /&gt;\r\n&lt;br /&gt;\r\nMỹ nữ hội trưởng: &amp;quot;Phong ca, thập bội tốc độ cảm gi&amp;aacute;c, ng&amp;ocirc;, thực sự thật sự rất tốt thoải m&amp;aacute;i đ&amp;acirc;u. . .&amp;quot;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/vong-du-chi-ta-co-10-lan-toc-do-danh&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:30:28', '2024-11-01 11:39:25', 1, 1, 41, 1, 'time_1722139820_file_banquyen.pdf'),
(25, 'Từ Arknights Bắt Đầu Xây Dựng Thế Lực', 'time_1722140206_file_Từ Arknights Bắt Đầu Xây Dựng Thế Lực.jpg', '&lt;p&gt;L&amp;acirc;u l&amp;acirc;u muốn đổi mới m&amp;ocirc; tả m&amp;agrave; chả biết viết g&amp;igrave;, th&amp;ocirc;i th&amp;igrave; t&amp;oacute;m tắt tạm vậy.&lt;br /&gt;\r\nMap ch&amp;iacute;nh: Terra (Arknights), bắt đầu từ khoảng 20 năm trước c&amp;aacute;c sự kiện ch&amp;iacute;nh.&lt;br /&gt;\r\nTương lai sẽ bay qua bay lại c&amp;aacute;c thế giới kh&amp;aacute;c, nhưng sẽ chỉ giới hạn trong anime, manga. T&amp;aacute;c kh&amp;ocirc;ng viết map truyện Trung. Nguy&amp;ecirc;n nh&amp;acirc;n? Do t&amp;aacute;c kh&amp;ocirc;ng th&amp;iacute;ch, với lại nh&amp;eacute;t truyện trung v&amp;agrave;o manga, anime n&amp;oacute; kỳ kỳ sao ấy, n&amp;ecirc;n th&amp;ocirc;i, kh&amp;ocirc;ng truyện trung nh&amp;eacute;.&lt;br /&gt;\r\n: )&lt;br /&gt;\r\nBản đồ Terra: https://i.pinimg.com/564x/de/18/2c/de182c676fc22a2e930f34b3be8ccc90.jpg&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn truyện:&amp;nbsp;https://metruyencv.com/truyen/tu-arknights-bat-dau-xay-dung-the-luc&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:30:37', '2024-11-01 11:27:50', 1, 1, 40, 1, 'time_1722140206_file_banquyen.pdf'),
(26, 'Xuyên Về Cổ Đại Làm Tiểu Cô Nương Lợi Hại', 'time_1731615156_file_Xuyên về cổ đại làm tiểu cô nương.png', '&lt;p&gt;L&amp;ecirc; Tường bưng một m&amp;acirc;m lớn đựng đầy thức ăn bỏ l&amp;ecirc;n tr&amp;ecirc;n b&amp;agrave;n, nh&amp;igrave;n một b&amp;agrave;n đồ ăn đầy tr&amp;agrave;n m&amp;agrave; trong l&amp;ograve;ng cũng cảm thấy thỏa m&amp;atilde;n.&lt;br /&gt;\r\n&lt;br /&gt;\r\nTuy n&amp;oacute;i vận mệnh đối với c&amp;ocirc; c&amp;oacute; hơi bất c&amp;ocirc;ng, từ nhỏ đ&amp;atilde; mất đi người th&amp;acirc;n, lại bị ung thư dạ d&amp;agrave;y, nhưng c&amp;ocirc; vẫn lu&amp;ocirc;n c&amp;oacute; tri kỉ tốt bầu bạn ở b&amp;ecirc;n cạnh, sự nghiệp cũng thuận buồm xu&amp;ocirc;i gi&amp;oacute;, gần nửa đời người cũng sống trong vui sướng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ldquo;Kiều Kiều, cậu mau nếm thử đi!&amp;rdquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\nL&amp;acirc;m Kiều đem sự chua s&amp;oacute;t nhịn xuống, sau đ&amp;oacute; đưa tay tiếp nhận chiếc đũa, cực k&amp;igrave; nể t&amp;igrave;nh m&amp;agrave; gắp v&amp;agrave;i gắp đồ ăn, một b&amp;ecirc;n v&amp;ugrave;i đầu ăn một b&amp;ecirc;n lại khen ngợi kĩ năng nấu nướng của c&amp;ocirc; bạn m&amp;igrave;nh. Nước mắt rơi cả v&amp;agrave;o trong b&amp;aacute;t m&amp;agrave; c&amp;ocirc; cũng kh&amp;ocirc;ng d&amp;aacute;m ngẩng đầu, bởi v&amp;igrave; c&amp;ocirc; sợ bị bạn m&amp;igrave;nh nh&amp;igrave;n thấy.&lt;br /&gt;\r\n&lt;br /&gt;\r\nR&amp;otilde; r&amp;agrave;ng mới c&amp;oacute; 28 tuổi, c&amp;ograve;n trẻ như vậy, m&amp;agrave; đ&amp;atilde; mắc bệnh ung thư thời k&amp;igrave; cuối, c&amp;ocirc; cứ trơ mắt nh&amp;igrave;n bạn tốt như một đ&amp;oacute;a hoa từ từ kh&amp;ocirc; h&amp;eacute;o m&amp;agrave; kh&amp;ocirc;ng c&amp;oacute; c&amp;aacute;ch n&amp;agrave;o. Mỗi khi nh&amp;igrave;n thấy đỉnh đầu trọc lốc của L&amp;ecirc; Tường l&amp;agrave; c&amp;ocirc; liền kh&amp;oacute; chịu như muốn đ&amp;ograve;i mạng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://truyenfull.io/xuyen-ve-co-dai-lam-tieu-co-nuong-loi-hai/chuong-1&lt;/p&gt;', '2024-11-15 03:12:36', '2024-11-15 03:12:36', 1, 1, 42, 1, 'time_1731610646_file_time_1722136017_file_banquyen.pdf'),
(27, 'Cố Tiểu Tây', 'time_1731698000_file_cố tiểu tây.jpg', '&lt;p&gt;&amp;quot;Cố Tiểu T&amp;acirc;y, c&amp;ocirc; thật kh&amp;ocirc;ng biết xấu hổ, lớn l&amp;ecirc;n th&amp;agrave;nh bộ dạng n&amp;agrave;y c&amp;ograve;n d&amp;aacute;m d&amp;acirc;y dưa với anh t&amp;ocirc;i? C&amp;ocirc; cũng kh&amp;ocirc;ng soi mặt v&amp;agrave;o trong nước tiểu m&amp;agrave; xem bản th&amp;acirc;n m&amp;igrave;nh!&amp;quot;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;quot;Nh&amp;agrave; t&amp;ocirc;i l&amp;agrave; căn ch&amp;iacute;nh mi&amp;ecirc;u hồng, gia đ&amp;igrave;nh chiến sĩ thi đua quang vinh, c&amp;ocirc; th&amp;igrave; sao? &amp;Ocirc;ng nội c&amp;ocirc; l&amp;agrave; địa chủ! Mẹ c&amp;ocirc; cũng kh&amp;ocirc;ng cần c&amp;ocirc;, chạy theo người trong th&amp;agrave;nh, cha c&amp;ocirc; anh trai c&amp;ocirc; cũng đều l&amp;agrave; kẻ đầu đường x&amp;oacute; chợ, c&amp;ograve;n c&amp;oacute; mặt mũi bu b&amp;aacute;m anh t&amp;ocirc;i?&amp;quot;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;quot;Mau c&amp;uacute;t! Nếu kh&amp;ocirc;ng t&amp;ocirc;i sẽ lấy ph&amp;acirc;n tr&amp;acirc;u giội đầu c&amp;ocirc;!&amp;quot;&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://truyenfull.io/co-tieu-tay/chuong-1&lt;/p&gt;', '2024-11-16 02:14:19', '2024-11-16 02:14:19', 1, 1, 42, 1, 'time_1731698000_file_time_1722136017_file_banquyen.pdf'),
(28, 'Từ giới giải trí đến nhà giàu số 1', 'time_1731701233_file_Từ giới giải trí.jpg', '&lt;p&gt;Dương Lỗi đột nhi&amp;ecirc;n nhận được một hệ thống đầy b&amp;iacute; ẩn, cho ph&amp;eacute;p anh đổi to&amp;agrave;n bộ đồ vật bằng danh vọng. Mặc d&amp;ugrave; anh theo chủ nghĩa v&amp;ocirc; thần, nhưng sự xuất hiện của hệ thống đ&amp;atilde; đảo lộn tưởng tượng của anh.&lt;br /&gt;\r\n&lt;br /&gt;\r\nMột ng&amp;agrave;y, khi anh đang học tại đại học, anh đột nhi&amp;ecirc;n bị ngất xỉu v&amp;agrave; sau đ&amp;oacute; nhận được hệ thống. Điều n&amp;agrave;y dẫn anh đến suy nghĩ rằng hai sự kiện n&amp;agrave;y c&amp;oacute; li&amp;ecirc;n quan đến nhau.&lt;br /&gt;\r\n&lt;br /&gt;\r\nKhi anh thấy những biểu tượng tr&amp;ecirc;n giao diện thay đổi, anh nhanh ch&amp;oacute;ng nhận thấy rằng anh c&amp;oacute; thể tiến v&amp;agrave;o một thế giới tiểu thuyết. Trong gi&amp;acirc;y l&amp;aacute;t, anh được đưa đến một thế giới ho&amp;agrave;n to&amp;agrave;n mới.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://truyenfull.io/tu-gioi-giai-tri-den-nha-giau-so-1/&lt;/p&gt;', '2024-11-16 03:13:25', '2024-11-16 03:13:25', 1, 1, 45, 1, 'time_1731701233_file_time_1722140206_file_banquyen.pdf');

